  gt()%>%
  # Add title and subtitle
  tab_header(
    title = "Ridership Report by Jurisdiction",
    # Use markdown syntax with md()
    subtitle = md("Spring **2022**"))%>%
  tab_style(
    # Select object to modify
    locations = cells_title(groups = 'title'),
    # Specify text style
    style = list(
      cell_text(
        font=google_font(name = 'Nunito'), 
        size='xx-large',
        color='darkblue'
  )))


